mapper.select:
WITH RECURSIVE UserMenuCTE AS ( SELECT u.id AS user_id, u.username, m.id AS menu_id, m.menu_name, m.parent_id FROM user u INNER JOIN role_user ru ON u.id = ru.user_id INNER JOIN role_menu rm ON ru.role_id = rm.role_id INNER JOIN menu m ON rm.menu_id = m.id WHERE u.id = #{userId}
UNION ALL
SELECT cte.user_id, cte.username, m.id, m.menu_name, m.parent_id FROM menu m INNER JOIN UserMenuCTE cte ON m.id = cte.parent_id ) SELECT DISTINCT menu_name FROM UserMenuCTE;
